mapper.select:
SELECT RSEQ, RPDSEQ, MID, PNAME, RDATE, TITLE, CONTENT, PIC0, PIC1, PIC2, PIC3, RLIKE FROM ( SELECT ROW_NUMBER() OVER(ORDER BY RSEQ DESC) AS RNUM, RSEQ, RPDSEQ, MID, PNAME, RDATE, TITLE, CONTENT, PIC0, PIC1, PIC2, PIC3, RLIKE FROM WREVIEW WHERE RPDSEQ=#{rpdseq} AND 1=1
AND MID LIKE '%'||#{s_keyword}||'%'
AND CONTENT LIKE '%'||#{s_keyword}||'%'
) A WHERE RNUM BETWEEN ${start} AND ${end}

SELECT RSEQ, RPDSEQ, MID, PNAME, RDATE, TITLE, CONTENT, PIC0, PIC1, PIC2, PIC3, RLIKE FROM ( SELECT ROW_NUMBER() OVER(ORDER BY RSEQ DESC) AS RNUM, RSEQ, RPDSEQ, MID, PNAME, RDATE, TITLE, CONTENT, PIC0, PIC1, PIC2, PIC3, RLIKE FROM WREVIEW WHERE MID=#{mid} AND 1=1
AND TITLE LIKE '%'||#{s_keyword}||'%'
AND CONTENT LIKE '%'||#{s_keyword}||'%'
) A WHERE RNUM BETWEEN ${start} AND ${end}

mapper.update:
UPDATE WREVIEW
SET TITLE = #{title}, CONTENT = #{content}, PIC0=#{pic0}, PIC1=#{pic1}, PIC2=#{pic2}, PIC3=#{pic3}
WHERE RSEQ=#{rseq}

UPDATE WREVIEW
SET RLIKE = RLIKE + 1
WHERE RSEQ=#{rseq}

UPDATE WREVIEW
SET RLIKE = RLIKE - 1
WHERE RSEQ=#{rseq}
